import React from "react";
import "./SingleRoute.css";
import { useParams } from "react-router-dom";
import { PRODUCTS } from "../../static/index";

function SingleRoute() {
  const params = useParams();
  const item = PRODUCTS?.find((el) => el._id === params._id);
  return (
    <div className="singleRoute  container">
      <div className="singleroute__wrapper">
        <div className="singleroute__card">
          <div className="singleroute__img">
            <img src={item.url} alt="" />
          </div>
          <div className="singleroute__price">
            <h3 className="card__title">{el.title}</h3>
            <mark>{((el.price * 1.5) / 12)?.brm()} so'm</mark>
            <del>{(item.price * 1.2)?.brm()}</del>
            <p>{item.price?.brm()} so'm</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SingleRoute;
