import React, { useState } from "react";
import "./Navbar.css";
import { IoMenu, IoSearch } from "react-icons/io5";
import { FaRegUser, FaRegHeart, FaCartArrowDown } from "react-icons/fa";
import { IoHome } from "react-icons/io5";
import { NavLink } from "react-router-dom";

function Navbar() {
  const [value, setValue] = useState("");
  console.log(value);
  return (
    <div className="navbar__wrapper">
      <div className="container">
        <div className="navbar">
          <NavLink to={"/"} className="navbar__logo">
            <h2>Uzum market</h2>
          </NavLink>
          <button className="navbar__category-btn">
            <IoMenu />
            <span>Katalog</span>
          </button>
          <div className="navbar__search">
            <input
              value={value}
              onChange={(e) => setValue(e.target.value)}
              type="text"
              placeholder="Search..."
            />
            <button>
              <IoSearch />
            </button>
            {value.trim() && <div className="nav__search-drop"></div>}
          </div>
          <div className="navbar__collection">
            <NavLink to={"/"} className="navbar__item">
              <IoHome />
              <span>Bosh Sahifa</span>
            </NavLink>
            <NavLink to={"/login"} className="navbar__item">
              <FaRegUser />
              <span>Kirish</span>
            </NavLink>
            <NavLink to={"/wishes"} className="navbar__item">
              <FaRegHeart />
              <span>Sevimlilar</span>
            </NavLink>
            <NavLink to={"/cart"} className="navbar__item">
              <FaCartArrowDown />
              <span>Savat</span>
            </NavLink>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
